CREATE OR REPLACE FUNCTION obter_etapas_processo_sol ( ie_tipo_solucao_p bigint, nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS bigint AS $body$
DECLARE


nr_etapas_w	smallint := 0;


BEGIN
if (ie_tipo_solucao_p IS NOT NULL AND ie_tipo_solucao_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then
	begin
	if (ie_tipo_solucao_p = 1) then
		begin
		select	count(*)
		into STRICT	nr_etapas_w
		from	adep_processo
		where	nr_prescricao	= nr_prescricao_p
		and	nr_seq_solucao	= nr_seq_solucao_p
		and	coalesce(dt_cancelamento::text, '') = '';
		end;
	end if;
	end;
end if;
return nr_etapas_w;
end;
