CREATE OR REPLACE FUNCTION obter_etapas_solucao ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_etapa_adep_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_retorno_aux_w	varchar(50);

C01 CURSOR FOR
	SELECT	nr_etapa_sol
	FROM	prescr_mat_hor
	WHERE	nr_prescricao = nr_prescricao_p
	AND	nr_seq_solucao = nr_seq_solucao_p
	AND	nr_etapa_sol > nr_etapa_adep_p
	AND	coalesce(dt_suspensao::text, '') = ''
	AND	ie_agrupador = 4
	AND	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S'
	GROUP BY dt_horario,
		 nr_etapa_sol
	ORDER BY dt_horario,
		 nr_etapa_sol;


BEGIN

OPEN c01;
LOOP
FETCH c01 INTO ds_retorno_aux_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_retorno_w := ds_retorno_w||','||ds_retorno_aux_w;
END LOOP;
CLOSE c01;

ds_retorno_w	:=	SUBSTR(ds_retorno_w,2,LENGTH(ds_retorno_w)-1);

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
