CREATE OR REPLACE FUNCTION obter_etapa_ativ_sup_1 (nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_etapa_w		bigint;
nr_seq_superior_w	bigint;

nr_seq_superior_ww	bigint;
vl_retorno_w		bigint;
achou 	boolean;


BEGIN

achou := false;

select  coalesce(nr_seq_superior,0),
	coalesce(nr_seq_etapa,0)
into STRICT	nr_seq_superior_w,
	nr_seq_etapa_w
from	proj_cron_etapa c
where	c.nr_sequencia = nr_sequencia_p;
nr_seq_superior_ww := nr_seq_superior_w;

if nr_seq_superior_ww = 0 then
	nr_seq_superior_ww := nr_sequencia_p;
end if;

while (nr_seq_superior_w <> 0 AND nr_seq_etapa_w = 0) and (not achou ) loop
	select  coalesce(nr_seq_superior,0),
		coalesce(nr_seq_etapa,0)
	into STRICT	nr_seq_superior_w,
		nr_seq_etapa_w
	from	proj_cron_etapa c
	where	c.nr_sequencia = nr_seq_superior_w;

	if (nr_seq_superior_w = nr_seq_superior_ww) then
		achou := true;
	end if;

	if (nr_seq_superior_w = 0) or (nr_seq_etapa_w <> 0) then
		achou := true;
	end if;

	if not achou then
		nr_seq_superior_ww := nr_seq_superior_w;
	end if;


end loop;

return	nr_seq_superior_ww;

end;
$body$
