CREATE OR REPLACE FUNCTION obter_etapa_atual_sol_agrupada (nr_seq_cpoe_p prescr_material.nr_seq_mat_cpoe%type) RETURNS bigint AS $body$
DECLARE


nr_etapa_atual_w     bigint;


BEGIN

select	coalesce(max(buscar_etapa_sol_agrupada(a.nr_prescricao, b.nr_seq_mat_cpoe, a.nr_etapa_sol)),0)
into STRICT	nr_etapa_atual_w
from	prescr_mat_hor a,
        prescr_material b
where	Obter_se_horario_liberado(a.dt_lib_horario, a.dt_horario) = 'S'
and	(a.dt_inicio_horario IS NOT NULL AND a.dt_inicio_horario::text <> '')
and     b.nr_seq_mat_cpoe = nr_seq_cpoe_p
and     a.nr_prescricao = b.nr_prescricao
and     a.nr_seq_solucao = b.nr_sequencia_solucao;

if (nr_etapa_atual_w = 0) then
	select	coalesce(min(buscar_etapa_sol_agrupada(a.nr_prescricao, b.nr_seq_mat_cpoe, a.nr_etapa_sol)),0)
	into STRICT	nr_etapa_atual_w
	from	prescr_mat_hor a,
		prescr_material b
	where	Obter_se_horario_liberado(a.dt_lib_horario, a.dt_horario) = 'S'
	and	coalesce(a.dt_suspensao::text, '') = ''
	and     b.nr_seq_mat_cpoe = nr_seq_cpoe_p
	and     a.nr_prescricao = b.nr_prescricao
	and	a.nr_seq_solucao = b.nr_sequencia_solucao;
end if;

return nr_etapa_atual_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
