SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_etapa_solucao_susp ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_seq_esquema_p bigint) RETURNS bigint AS $body$
DECLARE


nr_etapa_w	bigint;
nr_sequencia_w	bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	prescr_solucao_esquema
where	nr_prescricao	= nr_prescricao_p
and	nr_seq_solucao	= nr_seq_solucao_p
order by 1;


BEGIN
if (coalesce(nr_seq_esquema_p,0) > 0) then
	nr_etapa_w	:= 1;
	open c01;
	loop
	fetch c01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		if (nr_sequencia_w	< nr_seq_esquema_p) then
			nr_etapa_w	:= nr_etapa_w + 1;
		end if;
	end loop;
	close c01;
end if;

return	nr_etapa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
