CREATE OR REPLACE FUNCTION obter_etapa_sol_dialise ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		prescr_mat_hor.nr_etapa_sol%type;


BEGIN
qt_retorno_w := 0;
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then

	select	coalesce(max(nr_etapa_sol),0)
	into STRICT	qt_retorno_w
	from	prescr_mat_hor
	where	nr_prescricao = nr_prescricao_p
	and		nr_seq_solucao = nr_seq_solucao_p
	and		coalesce(dt_suspensao::text, '') = ''
	and		obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

end if;

return qt_retorno_w;

end;
$body$
