CREATE OR REPLACE FUNCTION obter_etiqueta_bolsa_hm ( nr_seq_horario_p bigint, nr_prescricao_p bigint default null, nr_seq_item_p bigint default null, cd_hemocomponente_p text default null, cd_bolsa_p text default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1000);


BEGIN

if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then

	SELECT	obter_select_concatenado_bv('	SELECT	cd_bolsa || nr_bolsa_isbt
											FROM	prescr_proc_bolsa
											WHERE	nr_seq_horario 		  = :nr_seq_horario_p
											AND	dt_cancelamento is null
											AND	nvl(ie_bipado,''N'')    = ''N'' ',
											'nr_seq_horario_p=' || nr_seq_horario_p, ',')
	into STRICT	ds_retorno_w
	;

else

	SELECT	obter_select_concatenado_bv('	SELECT	cd_bolsa || nr_bolsa_isbt
											FROM	prescr_proc_bolsa
											WHERE	nr_prescricao 		  = :nr_prescricao_p
											AND	nr_seq_procedimento = :nr_seq_proc_p
											AND	dt_cancelamento is null
											AND cd_bolsa =:cd_bolsa_p
											AND nr_bolsa_isbt =:nr_bolsa_isbt_p
											AND	nvl(ie_bipado,''N'')    = ''N'' ',
											'nr_prescricao_p=' || nr_prescricao_p||
											';nr_seq_proc_p='||nr_seq_item_p||
											';cd_bolsa_p='||cd_bolsa_p||
											';nr_bolsa_isbt_p='||cd_hemocomponente_p, ';')
	into STRICT	ds_retorno_w
	;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
