CREATE OR REPLACE FUNCTION obter_etiqueta_sne ( nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


ds_etiqueta_w	varchar(255);


BEGIN

if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then

	SELECT	SUBSTR(obter_codigo_barras_ordem_frac(nr_seq_etiqueta),1,14)
	into STRICT	ds_etiqueta_w
	FROM	prescr_mat_hor
	WHERE	nr_sequencia = nr_seq_horario_p;

	if (coalesce(ds_etiqueta_w::text, '') = '') then
		SELECT	nr_prescricao || nr_seq_material || nr_etapa_sol
		into STRICT	ds_etiqueta_w
		FROM	prescr_mat_hor
		WHERE	nr_sequencia = nr_seq_horario_p;
	end if;

end if;

return	ds_etiqueta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
