CREATE OR REPLACE FUNCTION obter_evento_adep (nr_seq_horario_p bigint, ie_tipo_item_p text, ie_evento_p text) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w	bigint;


BEGIN
if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') and (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (ie_evento_p IS NOT NULL AND ie_evento_p::text <> '') then
	if (ie_tipo_item_p = 'D') then
		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	prescr_mat_alteracao
		where	nr_seq_horario_dieta	= nr_seq_horario_p
		and	ie_alteracao		= ie_evento_p;

	elsif (ie_tipo_item_p in ('S','M','MAT')) then
		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	prescr_mat_alteracao a
		where	nr_seq_horario	= nr_seq_horario_p
		and	ie_alteracao	= ie_evento_p;

	elsif (ie_tipo_item_p in ('P','G','C','L')) then
		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	prescr_mat_alteracao
		where	nr_seq_horario_proc	= nr_seq_horario_p
		and	ie_alteracao		= ie_evento_p;

	elsif (ie_tipo_item_p = 'R') then
		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	prescr_mat_alteracao
		where	nr_seq_horario_rec	= nr_seq_horario_p
		and	ie_alteracao	= ie_evento_p;

	elsif (ie_tipo_item_p = 'E') then
		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	prescr_mat_alteracao
		where	nr_seq_horario_sae	= nr_seq_horario_p
		and	ie_alteracao	= ie_evento_p;
	
	elsif (ie_tipo_item_p = 'LD') then
		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	prescr_mat_alteracao
		where	nr_seq_horario	= nr_seq_horario_p
		and		ie_alteracao	= ie_evento_p
		and		coalesce(ie_evento_valido, 'S') = 'S';
	end if;
end if;

return nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
