CREATE OR REPLACE FUNCTION obter_evento_comunic_financ ( nr_sequencia_p bigint, cd_evento_p text) RETURNS varchar AS $body$
DECLARE


ie_permite_w		varchar(1);
cd_funcao_w		integer;

c01 CURSOR FOR
SELECT	cd_funcao
from	fin_regra_envio_comunic
where	nr_sequencia	= nr_sequencia_p;


BEGIN

ie_permite_w	:= 'N';

OPEN C01;
LOOP
FETCH C01 into
	cd_funcao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if (ie_permite_w = 'N') then
	
		if (cd_funcao_w	= 8559) and (cd_evento_p	in ('1')) then
			ie_permite_w	:= 'S';
		elsif (cd_funcao_w	= 89) and (cd_evento_p	in ('2')) then
			ie_permite_w	:= 'S';
		elsif (cd_funcao_w	= 857) and (cd_evento_p	in ('3')) then
			ie_permite_w	:= 'S';
		elsif (cd_funcao_w	= 813) and (cd_evento_p	in ('4', '6')) then
			ie_permite_w	:= 'S';
		elsif (cd_funcao_w	= 815) and (cd_evento_p	in ('5')) then
			ie_permite_w	:= 'S';
		end if;
		
	end if;
end loop;
close c01;

return ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
