CREATE OR REPLACE FUNCTION obter_evento_exibir_painel (nr_cirurgia_p bigint, nr_seq_pepo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN
if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') or (nr_seq_pepo_p IS NOT NULL AND nr_seq_pepo_p::text <> '') then
	select  max(substr(ds_evento,1,200))
	into STRICT	ds_retorno_w
	from    evento_cirurgia a,
		evento_cirurgia_paciente b
	where   a.nr_sequencia = b.nr_seq_evento
	and (coalesce(a.ie_exibir_painel,'N') = 'S')
	and	((b.nr_cirurgia = nr_cirurgia_p) or (b.nr_seq_pepo = nr_seq_pepo_p))
	and	coalesce(b.ie_situacao,'A') = 'A'
	and 	b.dt_registro = 	(SELECT max(y.dt_registro)
					from    evento_cirurgia x,
		  				evento_cirurgia_paciente y
					where   x.nr_sequencia = y.nr_seq_evento
					and (coalesce(x.ie_exibir_painel,'N') = 'S')
					and	coalesce(y.ie_situacao,'A') = 'A'
					and	((y.nr_cirurgia = nr_cirurgia_p) or (y.nr_seq_pepo = nr_seq_pepo_p)));
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
