CREATE OR REPLACE FUNCTION obter_evento_prob_arvore ( nr_seq_problema_p bigint, ie_tipo_p text, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(1) := 'S';

-- nr_seq_problema_p Passado como parâmetro para pode utilizar depois que conter liberação por usuário, especialidade entre outros.
BEGIN


If (nr_seq_problema_p IS NOT NULL AND nr_seq_problema_p::text <> '') then


	Select  coalesce(max(ie_apresentar_eventos),'S')
	into STRICT	ds_retorno_w
	from	cadastro_problema_arvore
	where	ie_tipo = ie_tipo_p
	and		coalesce(ie_situacao,'A') = 'A';


end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
