CREATE OR REPLACE FUNCTION obter_evento_regra_envio (ie_evento_disp_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_evento_w			bigint := 0;
nr_seq_regra_envio_sms_w	bigint;


BEGIN

select  coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_regra_envio_sms_w
from  	regra_envio_sms
where 	ie_evento_disp = ie_evento_disp_p
and	coalesce(ie_situacao,'A') = 'A';

if (nr_seq_regra_envio_sms_w > 0) then

	select 	coalesce(max(nr_seq_evento),0)
	into STRICT	nr_seq_evento_w
	from  	regra_envio_sms
	where 	nr_sequencia = nr_seq_regra_envio_sms_w
	and	coalesce(ie_situacao,'A') = 'A';

end if;

return	nr_seq_evento_w;

end;
$body$
