CREATE OR REPLACE FUNCTION obter_evolucoes ( dt_inicial_p timestamp, dt_final_p timestamp, cd_setor_atend_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(2000);
nr_atendimento_w	bigint;
cd_evolucao_w		bigint;

C01 CURSOR FOR 
SELECT	distinct a.nr_atendimento 
from	evolucao_paciente_lib_v a 
where	a.dt_evolucao between dt_inicial_p and dt_final_p 
 and	((a.cd_setor_atendimento = cd_setor_atend_p) or (0 = cd_setor_atend_p));


BEGIN 
 
OPEN C01;
LOOP 
  FETCH C01 into 
	nr_atendimento_w;
  EXIT WHEN NOT FOUND; /* apply on C01 */
	select	coalesce(max(a.cd_evolucao),0) 
	into STRICT	cd_evolucao_w 
	from	evolucao_paciente_lib_v a 
	where	a.nr_atendimento = nr_atendimento_w 
	and	a.dt_evolucao between dt_inicial_p and dt_final_p 
	and	((a.cd_setor_atendimento = cd_setor_atend_p) or (0 = cd_setor_atend_p));
	 
	ds_retorno_w	:= ds_retorno_w ||cd_evolucao_w||',';
	 
END LOOP;
CLOSE C01;
 
ds_retorno_w	:= substr(ds_retorno_w,1,length(ds_retorno_w)-1);
 
return	ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
