SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_exames_anterior_etapa (nr_prescricao_p bigint, ie_status_atend_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


nm_exame_w		varchar(20);
ds_retorno_w		varchar(4000);

c01 CURSOR FOR
		SELECT  substr(nm_exame,1,20)
		from 	exame_laboratorio a,
			prescr_procedimento b
		where 	a.nr_seq_exame = b.nr_seq_exame
		and	b.nr_prescricao = nr_prescricao_p
		and	b.ie_suspenso = 'N'
		and	coalesce(OBTER_SE_MOTIVO_BAIXA_CONTA(cd_motivo_baixa),'S') = 'S'
		and (ie_status_atend < ie_status_atend_p
			or a.ie_restricao  = 'S');



BEGIN

ds_retorno_w := '';

open c01;
loop
	fetch c01 into
			nm_exame_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	ds_retorno_w	:= ds_retorno_w || nm_exame_w || ';';

end loop;
close c01;


if (ie_opcao_p = 1) and (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then

	ds_retorno_w	:= Wheb_mensagem_pck.get_texto(309543, 'DS_RETORNO_W='||ds_retorno_w); --'Exames Faltantes: ' || ds_retorno_w;
end if;

return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_exames_anterior_etapa (nr_prescricao_p bigint, ie_status_atend_p bigint, ie_opcao_p bigint) FROM PUBLIC;

