SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_exames_material (nr_prescricao_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_material_w		varchar(255);
ds_retorno_w		varchar(255);


c01 CURSOR FOR
	SELECT  	c.cd_exame
	FROM		exame_laboratorio c,
			prescr_medica b,
			prescr_procedimento a,
			material_exame_lab d
	WHERE		a.nr_prescricao 	= b.nr_prescricao
	AND		a.nr_seq_exame		= c.nr_seq_exame
	AND		d.cd_material_exame 	= a.cd_material_exame
	AND		a.nr_prescricao 	= nr_prescricao_p
	AND		d.nr_sequencia		= nr_seq_material_p
	AND		(a.cd_material_exame IS NOT NULL AND a.cd_material_exame::text <> '');


BEGIN
	OPEN c01;
	LOOP
	FETCH c01 INTO
		ds_material_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_retorno_w:=ds_retorno_w||SUBSTR(ds_material_w||' ',1,3)||',';
	END LOOP;

RETURN SUBSTR(trim(both ds_retorno_w || ''),1,LENGTH(ds_retorno_w)-1);
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
