SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_exames_matricial ( nr_prescricao_p bigint, nr_colunas_p bigint, nr_largura_total_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(4000) := '';
cd_procedimento_w	bigint;
ds_procedimento_w	varchar(255);
ds_cod_proc_w		varchar(255);

nr_espaco_w		smallint;



c01 CURSOR FOR
	SELECT	cd_procedimento,
		substr(obter_desc_prescr_proc(cd_procedimento,ie_origem_proced, nr_seq_proc_interno),1,240)
	from	prescr_procedimento
	where	nr_prescricao 	= nr_prescricao_p
	and	coalesce(dt_suspensao::text, '') = '';


BEGIN


/*Para melhores resultados, a largura total deve ser divisível pelo número de colunas   */

nr_espaco_w := round(dividir(nr_largura_total_p, nr_colunas_p));



if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_largura_total_p IS NOT NULL AND nr_largura_total_p::text <> '') then

	OPEN C01;
	LOOP
	FETCH C01 into
		cd_procedimento_w,
		ds_procedimento_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin


		ds_cod_proc_w	:= '  ' || cd_procedimento_w || ' ' || ds_procedimento_w;

		ds_cod_proc_w	:= substr(rpad(ds_cod_proc_w, nr_espaco_w,' '),1,nr_espaco_w);

		ds_retorno_w	:= ds_retorno_w || ds_cod_proc_w;

		end;
	END LOOP;
end if;

return ds_retorno_w;


End;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
