CREATE OR REPLACE FUNCTION obter_exames_pendentes (nr_prescricao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

retorno_w varchar(250) := ' ';

BEGIN
	declare
		nm_exame_w    	varchar(250);
		C01 CURSOR FOR
			SELECT	f.nm_exame
			FROM	lab_parametro d,
				prescr_procedimento b,
				prescr_medica c,
				exame_laboratorio f
			WHERE	b.nr_prescricao = c.nr_prescricao
			AND	c.cd_estabelecimento = d.cd_estabelecimento
			AND	b.nr_seq_exame    = f.nr_seq_exame
			AND     obter_ds_status_result_exame(b.nr_prescricao, b.nr_sequencia, nm_usuario_p, cd_estabelecimento_p) = Wheb_mensagem_pck.get_texto(309557) /*'Processando'*/
			AND	b.nr_prescricao = nr_prescricao_p;
		BEGIN
			OPEN C01;
				LOOP
					FETCH C01 into
						nm_exame_w;
					EXIT WHEN NOT FOUND; /* apply on c01 */
					begin
						if ( retorno_w = ' ')	then
							retorno_w :=  ' ' || Wheb_mensagem_pck.get_texto(309549, 'NM_EXAME_W='||nm_exame_w); --' Exames Pendentes: '||nm_exame_w;
						else
							retorno_w := retorno_w ||' - '|| nm_exame_w;
						end if;
					exception
						when others then
							begin
								RAISE NOTICE '%', Wheb_mensagem_pck.get_texto(1042279);
							end;
					end;
				END LOOP;
			CLOSE C01;
		END;
	return retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
