SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_exames_positivos_concat ( nr_seq_doacao_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(2000) := '';	
ds_exame_w	varchar(100);
			
C01 CURSOR FOR
	SELECT 	ds_exame	
	from	san_exame_lote a,
		san_exame_realizado b,
		san_exame c
	where	a.nr_sequencia 	= b.nr_seq_exame_lote
	and	c.nr_sequencia	= b.nr_seq_exame
	and	a.nr_seq_doacao	= nr_seq_doacao_p
	and (san_obter_destino_exame(b.nr_seq_exame,3) = 'S' or san_obter_destino_exame(b.nr_seq_exame,0) = 'S')
	and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
	and	coalesce(c.ie_fator_rh,'N') = 'N'
	and	coalesce(c.ie_consiste_result_pos,'S') = 'N'
	and not exists (SELECT 	1
			from	san_parametro p
			where	p.cd_estabelecimento = cd_estabelecimento_p
			and	p.nr_seq_exame_rh = b.nr_seq_exame)
	and (Upper(b.ds_resultado) IN ('POSITIVO', 'REAGENTE', 'INDETERMINADO')
		or exists (	select 1
				from	san_exame_realizado x,
					san_exame y,
					san_exame_valor z
				where	y.nr_sequencia = c.nr_sequencia
				and	y.nr_sequencia = x.nr_seq_exame
				and	x.nr_seq_exame = z.nr_seq_exame
				and	x.nr_seq_exame_lote = a.nr_sequencia
				and	x.ds_resultado = to_char(z.nr_sequencia)
				and	z.ie_bloq_sorologia = 'S'
				and 	coalesce(y.cd_tipo_resultado::text, '') = ''));
			

BEGIN

open C01;
loop
fetch C01 into	
	ds_exame_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_retorno_w := ds_retorno_w||ds_exame_w||', ';
	end;
end loop;
close C01;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	ds_retorno_w := substr(ds_retorno_w,1,length(ds_retorno_w)-2);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
