SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_exames_prescr_hsc (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


/*
Function criada para atender a OS37825 - HSC
*/
ds_procedimento_w 	PROCEDIMENTO.DS_PROCEDIMENTO%type;
ds_exames_w		varchar(2000);

c01 CURSOR FOR
SELECT	substr(obter_desc_prescr_proc(b.cd_procedimento, b.ie_origem_proced, a.nr_seq_proc_interno),1,240)
from	procedimento b,
	prescr_procedimento a
where	a.cd_procedimento		= b.cd_procedimento
and	a.ie_origem_proced		= b.ie_origem_proced
and	a.nr_prescricao		= nr_prescricao_p
and	a.cd_setor_atendimento	in (65,66,67,68,69,289);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	open c01;
	loop
	fetch c01 into	ds_procedimento_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	ds_exames_w	:= ds_exames_w || ds_procedimento_w || ', ';

end loop;
end if;

return	ds_exames_w;

END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
