CREATE OR REPLACE FUNCTION obter_exames_recoleta_lote_ext (nr_seq_lote_externo_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_status_recoleta_w		smallint;
cd_exame_w			varchar(20);
nr_prescricao_w			bigint;
nr_prescricao_ant_w		bigint;
ie_primeiro_w			varchar(1);
ds_retorno_w			varchar(4000);

c01 CURSOR FOR
	SELECT 	a.nr_prescricao,
		b.cd_exame
	from 	exame_laboratorio b,
		prescr_procedimento a
	where 	a.nr_seq_exame = b.nr_seq_exame
	and	a.nr_seq_lote_externo = nr_seq_lote_externo_p
	and	a.ie_status_atend     = ie_status_recoleta_w;


BEGIN

select 	coalesce(max(ie_status_recoleta),0)
into STRICT	ie_status_recoleta_w
from 	lab_parametro
where	cd_estabelecimento = cd_estabelecimento_p;

ds_retorno_w	:= '';
ie_primeiro_w	:= 'S';

open c01;
loop
fetch c01 into
	nr_prescricao_w,
	cd_exame_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	if ((nr_prescricao_w <> nr_prescricao_ant_w) or (coalesce(nr_prescricao_ant_w::text, '') = '')) then

		if (ie_primeiro_w = 'S') then
			ds_retorno_w := ds_retorno_w || obter_desc_expressao(325814) ||' '|| nr_prescricao_w ||' '|| obter_desc_expressao(621805) ||' '|| cd_exame_w;
		else
			ds_retorno_w := ds_retorno_w || chr(10) || obter_desc_expressao(325814) ||' '|| nr_prescricao_w ||' '|| obter_desc_expressao(621805) ||' '|| cd_exame_w;
		end if;
	else
		ds_retorno_w := ds_retorno_w || ' ' || cd_exame_w;
	end if;


	nr_prescricao_ant_w 	:=	nr_prescricao_w;
	ie_primeiro_w		:= 	'N';

end loop;
close c01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
