SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_exames_soropos_concat (cd_pessoa_fisica_p text, dt_doacao_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_exames_w		varchar(4000);
ds_exames_ww		varchar(4000);
C01 CURSOR FOR
	SELECT	e.ds_exame
	from	san_exame e,
		san_exame_realizado d,
		san_exame_lote c,
		san_doacao a
	where	1 = 1
	and	d.nr_seq_exame = e.nr_sequencia
	and	d.nr_seq_exame_lote = c.nr_sequencia
	and	c.nr_seq_doacao	= a.nr_sequencia
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p
	and	a.dt_doacao between trunc(dt_doacao_p) and trunc(dt_doacao_p)+86399/86400
	and	san_obter_destino_exame(d.nr_seq_exame, 3) = 'S'
	and	replace(upper(d.ds_resultado),' ','') in ('POSITIVO','REAGENTE','INDETERMINADO');


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		ds_exames_ww;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_exames_w := ds_exames_w||ds_exames_ww||', ';
		end;
	end loop;
	close C01;

	ds_exames_w := substr(ds_exames_w, 1, length(ds_exames_w)-2);

end if;

return	ds_exames_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
