CREATE OR REPLACE FUNCTION obter_exame_adic_agenda (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


ds_exame_w	varchar(240);
ds_lista_w	varchar(4000);

c01 CURSOR FOR
SELECT	substr(obter_exame_agenda(cd_procedimento,ie_origem_proced,nr_seq_proc_interno),1,240) ds_exame
from	agenda_paciente_proc
where	nr_sequencia = nr_seq_agenda_p
order by
	nr_seq_agenda;


BEGIN
ds_lista_w := null;
if (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	open c01;
	loop
	fetch c01 into ds_exame_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (coalesce(length(ds_lista_w),0) <= 760) then
			ds_lista_w := ds_lista_w || ds_exame_w || '; ';
		end if;
		end;
	end loop;
	close c01;
end if;

return ds_lista_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
