SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_exame_equip_mat_integr ( ds_exame_integr_p text, cd_material_integr_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_material_w		bigint;
nr_seq_exame_w			bigint;
resultado_w			varchar(255);

BEGIN

resultado_w :=  null;

select 	coalesce(max(a.nr_seq_material),null)
into STRICT	nr_seq_material_w
from	equipamento_lab b,
	lab_exame_equip a,
	exame_laboratorio d
where	a.cd_equipamento  = b.cd_equipamento
and	d.nr_seq_exame    = a.nr_seq_exame
and	upper(b.ds_sigla)	= ie_opcao_p
and	cd_material_integr	= cd_material_integr_p
and 	coalesce(cd_exame_equip, cd_exame_integracao) = ds_exame_integr_p;

if (coalesce(nr_seq_material_w::text, '') = '') then
	select 	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_material_w
	from	material_exame_lab
	where	coalesce(cd_material_integracao,cd_material_exame) = cd_material_integr_p;
end if;

if (nr_seq_material_w IS NOT NULL AND nr_seq_material_w::text <> '') then

	select 	coalesce(max(a.nr_seq_exame),null)
	into STRICT	nr_seq_exame_w
	from	equipamento_lab b,
		lab_exame_equip a,
		exame_laboratorio d
	where	a.cd_equipamento  = b.cd_equipamento
	and	d.nr_seq_exame    = a.nr_seq_exame
	and	upper(b.ds_sigla)	  = ie_opcao_p
	and	coalesce(a.nr_seq_material,nr_seq_material_w) = nr_seq_material_w
	and 	coalesce(cd_exame_equip, cd_exame_integracao)= ds_exame_integr_p;

	if (coalesce(nr_seq_exame_w::text, '') = '') then

		select 	coalesce(max(a.nr_seq_exame),null)
		into STRICT	nr_seq_exame_w
		from	equipamento_lab b,
			lab_exame_equip a,
			exame_laboratorio d,
			material_exame_lab e
		where	a.cd_equipamento  = b.cd_equipamento
		and	d.nr_seq_exame    = a.nr_seq_exame
		and	upper(b.ds_sigla)	  = ie_opcao_p
		and	a.nr_seq_material = e.nr_sequencia
		and	coalesce(cd_material_integracao,cd_material_exame) = cd_material_integr_p
		and 	coalesce(cd_exame_equip, cd_exame_integracao)= ds_exame_integr_p;

	end if;

	resultado_w	:= nr_seq_exame_w;

end if;

return resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
