CREATE OR REPLACE FUNCTION obter_exame_lab_regra_setor ( cd_estabelecimento_p bigint, nr_seq_exame_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(2000) 	:= null;
qt_dia_entrega_w		bigint:=null;
nr_seq_grupo_w			bigint:=null;
nr_seq_grupo_imp_w		bigint:=null;
dt_prescricao_w			timestamp;
ie_dia_semana_w			smallint;
qt_dia_entrega_geral_w		bigint:=null;
nr_seq_prioridade_w		smallint;
/*
ie_opcao_p
qte - dias entrega -> qt_dia_entrega
*/
C01 CURSOR FOR
	SELECT	coalesce(qt_dia_entrega,0),
		coalesce(nr_seq_prioridade,0)
	from exame_lab_regra_setor
	where coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0)
	  and ((ie_dia_semana = ie_dia_semana_w) or ((ie_dia_semana = 9) and (ie_dia_Semana_w not in (7,1))) or (coalesce(ie_dia_semana::text, '') = ''))
	  and coalesce(nr_seq_grupo, nr_seq_grupo_w) = nr_seq_grupo_w
	  and coalesce(nr_seq_grupo_imp, nr_seq_grupo_imp_w) = nr_seq_grupo_imp_w
	  and coalesce(nr_seq_exame, nr_seq_exame_p) = nr_seq_exame_p
	  and dt_prescricao_w	between to_date(to_char(dt_prescricao_w,'dd/mm/yyyy') || ' ' || coalesce(ds_hora_inicio,to_char(dt_prescricao_w,'hh24:mi')) || ':00','dd/mm/yyyy hh24:mi:ss')
				and to_date(to_char(dt_prescricao_w,'dd/mm/yyyy') || ' ' || coalesce(ds_hora_fim,to_char(dt_prescricao_w,'hh24:mi')) || ':00','dd/mm/yyyy hh24:mi:ss')
	order by coalesce(nr_seq_prioridade,0), coalesce(nr_seq_material,0), coalesce(nr_seq_exame,9999999999), coalesce(nr_seq_grupo,9999999999), coalesce(ie_dia_semana,0), nr_seq_grupo_imp_w, cd_setor_solicitacao;


BEGIN

/* obter grupo exame */

select 	max(coalesce(a.nr_seq_grupo,0)),
	max(coalesce(nr_seq_grupo_imp,0))
into STRICT	nr_seq_grupo_w,
	nr_seq_grupo_imp_w
from   	exame_laboratorio a
where  	a.nr_seq_exame = nr_seq_exame_p;

dt_prescricao_w	:= trunc(clock_timestamp());

select	obter_cod_dia_semana(dt_prescricao_w)
into STRICT	ie_dia_semana_w
;

qt_dia_entrega_geral_w	:= 0;

open C01;
loop
fetch C01 into
	qt_dia_entrega_w,
	nr_seq_prioridade_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (qt_dia_entrega_geral_w = 0) or (coalesce(nr_seq_prioridade_w,0) > 0) then
		qt_dia_entrega_geral_w	:= qt_dia_entrega_w;
	end if;

	end;
end loop;
close C01;

if 	ie_opcao_p = 'QTDE' then
	ds_retorno_w	:=	qt_dia_entrega_geral_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
