CREATE OR REPLACE FUNCTION obter_exame_linha (nr_etiqueta_p bigint, ie_linha_p bigint, nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_procedimento_w	varchar(255);
nm_medico_atend_w	varchar(255);
ds_retorno_w		varchar(255);

 
/* IE_OPCAO = 	'P' - CD_Procedimento - DS_Procedimento 
			'M' - NM_MÃ©dico - NM_Atendente*/
 
 
C01 CURSOR FOR 
	SELECT ds_procedimento, 
			nm_medico_atend 
	from (	SELECT ceil(rownum/5) nr_etiqueta, 
			row_number() OVER () AS nr_linha, 
			c.cd_procedimento ||' - '|| substr(obter_descricao_procedimento(c.cd_procedimento, c.ie_origem_proced),1,50)||' - '||c.qt_procedimento ds_procedimento, 
			substr(coalesce(obter_crm_medico(c.cd_medico_exec), wheb_mensagem_pck.get_texto(802854) || ' '),1,120) ||' - '|| substr(coalesce(obter_nome_pf(c.cd_medico_exec),wheb_mensagem_pck.get_texto(793287)),1,180) || ' - ' || a.nm_usuario_atend nm_medico_atend 
		from	atendimento_paciente a, 
			prescr_medica b, 
			prescr_procedimento c 
		where	a.nr_atendimento = nr_atendimento_p 
		and	a.nr_atendimento = b.nr_atendimento 
		and	b.nr_prescricao = c.nr_prescricao) alias11 
	where	nr_etiqueta = nr_etiqueta_p 
	and	nr_linha = ie_linha_p 
	order by 1;


BEGIN 
	OPEN C01;
	LOOP 
	FETCH C01 into 
		ds_procedimento_w, 
		nm_medico_Atend_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	END LOOP;
 
 
IF (ie_opcao_p = 'P') then 
		ds_retorno_w := ds_procedimento_w;
ELSIF (ie_opcao_p = 'M') then 
		ds_retorno_w := nm_medico_atend_w;
END IF;
 
RETURN	ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
