CREATE OR REPLACE FUNCTION obter_exame_medico_adic ( nr_prescricao_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_prescr_w		bigint;
ds_seq_prescr_w		varchar(255);

c01 CURSOR FOR
SELECT	nr_Seq_procedimento
from	prescr_proced_prof_adic
where	nr_prescricao = nr_prescricao_p
and		cd_profissional = cd_medico_p;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_prescr_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	ds_seq_prescr_w := ds_seq_prescr_w ||nr_seq_prescr_w ||',';

end loop;
close c01;



return	substr(ds_seq_prescr_w,1,length(ds_seq_prescr_w)-1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
