CREATE OR REPLACE FUNCTION obter_exame_procedimento ( cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_exame_w		bigint;
ie_controle_w		smallint;
cd_estabelecimento_w	smallint;

C01 CURSOR FOR
SELECT	nr_seq_exame,
	1
FROM	exame_laboratorio
WHERE	ie_solicitacao	= 'S'
AND	cd_procedimento = cd_procedimento_p
and (lab_obter_se_exame_lib(NR_SEQ_EXAME,cd_estabelecimento_w) = 'S')
AND	ie_origem_proced= ie_origem_proced_p

UNION

SELECT	a.nr_seq_exame,
	2
FROM	exame_laboratorio b,
	exame_lab_convenio a
WHERE	a.cd_procedimento = cd_procedimento_p
AND	a.ie_origem_proced = ie_origem_proced_p
AND	b.ie_solicitacao = 'S'
AND	b.nr_seq_exame = a.nr_seq_exame
and (lab_obter_se_exame_lib(B.NR_SEQ_EXAME,cd_estabelecimento_w) = 'S')
AND	((a.dt_inicio_vigencia < clock_timestamp()) OR (coalesce(a.dt_inicio_vigencia::text, '') = ''))
AND	((coalesce(a.cd_convenio::text, '') = '') OR (a.cd_convenio = obter_convenio_atendimento(nr_atendimento_p)))
ORDER BY 2,1;


BEGIN

select 	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from 	prescr_medica
where	nr_atendimento = nr_atendimento_p;

open C01;
loop
	fetch C01 into
		nr_seq_exame_w,
		ie_controle_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	nr_seq_exame_w	:= nr_seq_exame_w;
end loop;
close C01;

return nr_seq_exame_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
