SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_exame_solic_guia_unimed (nr_seq_pedido_p bigint) RETURNS varchar AS $body$
DECLARE


qt_char_max_rel_w	smallint := 925;
qt_char_lista_w	smallint := 0;
qt_char_var_w		smallint := 0;
ds_exame_w		varchar(240);
ds_exames_rel_w	varchar(1000);

c01 CURSOR FOR
SELECT	b.ds_exame
from	med_exame_padrao b,
	med_ped_exame_cod a
where	a.nr_seq_exame	= b.nr_sequencia
and	a.nr_seq_pedido	= nr_seq_pedido_p
order by
	a.nr_seq_apresent;


BEGIN
if (nr_seq_pedido_p IS NOT NULL AND nr_seq_pedido_p::text <> '') then
	open c01;
	loop
	fetch c01 into ds_exame_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		qt_char_lista_w := coalesce(length(ds_exames_rel_w),0);
		qt_char_var_w	:= coalesce(length(ds_exame_w),0)+2;

		if	((qt_char_lista_w + qt_char_var_w) <= qt_char_max_rel_w) then
			ds_exames_rel_w := ds_exames_rel_w || ds_exame_w || ', ';
		end if;
		end;
	end loop;
	close c01;
end if;

return substr(ds_exames_rel_w,1,length(ds_exames_rel_w) - 2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
