SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_exam_pend_bar (nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(32000);
ds_exam_w varchar(150);

C01 CURSOR FOR
SELECT   substr(obter_desc_exame_lab(b.nr_seq_exame,null,b.cd_procedimento, b.ie_origem_proced) ,1,100) ||
' '||b.dt_prev_execucao val
FROM	  prescr_procedimento b,
	   prescr_medica a
WHERE a.nr_atendimento  = nr_atendimento_p
	AND	a.nr_prescricao  = b.nr_prescricao
	AND	b.ie_suspenso  <> 'S'
	AND	b.ie_status_execucao <> 'BE'
	AND	coalesce(a.dt_suspensao::text, '') = ''
	AND	coalesce(b.dt_cancelamento::text, '') = ''
	AND	(b.nr_seq_exame IS NOT NULL AND b.nr_seq_exame::text <> '')
	AND	not exists (
SELECT 1
FROM	result_laboratorio
WHERE nr_prescricao = a.nr_prescricao
	AND	nr_seq_prescricao = b.nr_sequencia
	AND	(dt_coleta IS NOT NULL AND dt_coleta::text <> ''))
	AND	 b.ie_status_atend < 35

ORDER BY  a.nr_prescricao desc
;



BEGIN

open c01;
loop
fetch c01 into
		ds_exam_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := ds_exam_w;
	else
		ds_retorno_w := ds_retorno_w || ', ' || ds_exam_w;

	end if;
	end;
end loop;
close c01;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
