CREATE OR REPLACE FUNCTION obter_excecao_alerta ( nr_seq_evento_p bigint, cd_pessoa_fisica_p text, ie_forma_ev_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(1);


BEGIN

ds_retorno_w := 'N';

if (nr_seq_evento_p IS NOT NULL AND nr_seq_evento_p::text <> '' AND cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	SELECT 	CASE WHEN COUNT(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	FROM	ev_evento_regra_dest
	WHERE	nr_seq_evento 		= nr_seq_evento_p
	and	cd_pf_destino		= cd_pessoa_fisica_p
	and	ie_forma_ev		= ie_forma_ev_p
	AND	coalesce(ie_excecao,'N') 	= 'S';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
