CREATE OR REPLACE FUNCTION obter_excessao_usuario_diag ( nr_sequencia_p cpoe_rule_consist_diag.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

ds_retorno_w := 'S';

select 	coalesce(max('N'),'S')
into STRICT 	ds_retorno_w
from 	cpoe_rule_consist_user a,
      cpoe_rule_consist_diag b
where	a.nr_seq_cpoe_rule = nr_sequencia_p
and b.nr_sequencia = a.nr_seq_cpoe_rule
and coalesce(b.ie_situacao, 'A') = 'A';

if (ds_retorno_w = 'N') then

	select 	coalesce(max('N'), 'S')
	into STRICT	ds_retorno_w
	from 	cpoe_rule_consist_user a,
        cpoe_rule_consist_diag b
	where a.nm_usuario_regra = nm_usuario_p
	and	a.nr_seq_cpoe_rule = nr_sequencia_p
  and b.nr_sequencia = a.nr_seq_cpoe_rule
  and coalesce(b.ie_situacao, 'A') = 'A';

end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
