CREATE OR REPLACE FUNCTION obter_executor_desenv_item (nr_seq_func_item_p bigint, cd_funcao_p bigint, nr_seq_grupo_des_p bigint) RETURNS varchar AS $body$
DECLARE


nm_usuario_exec_w varchar(15) := '';


BEGIN

if (nr_seq_func_item_p > 0) and (cd_funcao_p > 0) then

	SELECT MIN(a.nm_usuario_exec)
		   into STRICT nm_usuario_exec_w
	FROM 	funcao_grupo_des a
	WHERE 	a.cd_funcao = cd_funcao_p
	and	a.nr_seq_grupo	= nr_seq_grupo_des_p
	and	coalesce(nr_seq_func_item::text, '') = ''
	AND NOT EXISTS (SELECT 1 FROM funcao_grupo_des_cli x
				    WHERE a.nr_sequencia = x.nr_seq_funcao_grupo);
end if;


return	nm_usuario_exec_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
