CREATE OR REPLACE FUNCTION obter_exibe_pasta_glicemia (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_cig_w	varchar(1) := 'S';
ie_ccg_w	varchar(1) := 'S';
ie_aux_w	varchar(1) := 'S';
ds_retorno_w	varchar(1) := 'S';


BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then

	select 	coalesce(max('S'),'N')
	into STRICT	ie_cig_w
	from 	atendimento_cig a
	where  	a.nr_atendimento = nr_atendimento_p;

	select 	coalesce(max('S'),'N')
	into STRICT	ie_ccg_w
	from 	atendimento_glicemia a
	where  	a.nr_atendimento = nr_atendimento_p;

	select 	coalesce(max('S'),'N')
	into STRICT	ie_aux_w
	from 	atend_glicemia a
	where  	a.nr_atendimento = nr_atendimento_p;

end if;

if (ie_cig_w = 'N') and (ie_aux_w = 'N') and (ie_ccg_w = 'N') then
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
