CREATE OR REPLACE FUNCTION obter_exibe_topografia_empresa (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';


BEGIN

select	CASE WHEN COUNT(1)=0 THEN 'S'  ELSE 'N' END
into STRICT	ie_retorno_w
from	topografia_dor
where	ie_situacao = 'A'
and		cd_empresa = obter_empresa_estab(wheb_usuario_pck.get_cd_estabelecimento);

if (coalesce(ie_retorno_w,'N') = 'N') then
	select	CASE WHEN COUNT(1)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_retorno_w
	from	topografia_dor
	where	ie_situacao = 'A'
	and		nr_sequencia = nr_sequencia_p
	and		cd_empresa = obter_empresa_estab(wheb_usuario_pck.get_cd_estabelecimento);
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
