CREATE OR REPLACE FUNCTION obter_exibir_asv_team (NR_SEQ_ATEND_UNIDADE_P bigint) RETURNS varchar AS $body$
DECLARE


IE_MOSTAR_ASV_TEAM_W SUBTIPO_EPISODIO.IE_MOSTAR_ASV_TEAM%TYPE;


BEGIN

  SELECT coalesce(MAX(S.IE_MOSTAR_ASV_TEAM),'N')
    INTO STRICT IE_MOSTAR_ASV_TEAM_W
  FROM
    ATEND_PACIENTE_UNIDADE AP,
    ATENDIMENTO_PACIENTE A,
    EPISODIO_PACIENTE EP,
    SUBTIPO_EPISODIO S
  WHERE 
  AP.NR_SEQ_INTERNO = NR_SEQ_ATEND_UNIDADE_P
  AND A.NR_ATENDIMENTO = AP.NR_ATENDIMENTO
  AND A.NR_SEQ_TIPO_ADMISSAO_FAT = S.NR_SEQ_TIPO_ADMISSAO 
  AND  EP.NR_SEQ_TIPO_EPISODIO = S.NR_SEQ_TIPO_EPISODIO
  AND  EP.NR_SEQUENCIA = A.NR_SEQ_EPISODIO
  ORDER BY A.DT_ATUALIZACAO DESC;
  
  RETURN IE_MOSTAR_ASV_TEAM_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
