CREATE OR REPLACE FUNCTION obter_exigencias_cad_material ( cd_material_p bigint, cd_material_estoque_p bigint, cd_classe_material_p bigint, ie_exigencia_p text) RETURNS varchar AS $body$
DECLARE


/*ie_exigencia_p - Refere-se qual é a exigencia que a function deve retornar, conforme a Regra Exigências ao cadastrar material
MA - Exige Marca
*/
ie_exige_w				varchar(1) := 'N';
ie_exige_marca_w				varchar(1) := 'N';
cd_grupo_material_w			bigint;
cd_subgrupo_material_w			bigint;

c01 CURSOR FOR
SELECT	coalesce(ie_exige_marca,'S')
from	regra_exige_cadastro_mat
where	coalesce(cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
and	coalesce(cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
and	coalesce(cd_classe_material, cd_classe_material_p)		= cd_classe_material_p
and (coalesce(cd_material, cd_material_p) 			= cd_material_p or cd_material_p = 0)
and	((ie_excecao = 'T') or
	(ie_excecao = 'C' AND cd_material_p <> cd_material_estoque_p))
order by
	coalesce(cd_material, 0),
	coalesce(cd_classe_material, 0),
	coalesce(cd_subgrupo_material, 0),
	coalesce(cd_grupo_material, 0);


BEGIN

select	a.cd_grupo_material,
	a.cd_subgrupo_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w
from	estrutura_material_v a
where	cd_classe_material = cd_classe_material_p
group by a.cd_grupo_material,
	a.cd_subgrupo_material;

open C01;
loop
fetch C01 into
	ie_exige_marca_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_exige_w := ie_exige_w;
	end;
end loop;
close C01;

if (ie_exigencia_p = 'MA') then
	ie_exige_w := ie_exige_marca_w;
end if;

return	ie_exige_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
