SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_exige_dec_obito_js (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_exige_declaracao_w	varchar(1);
cd_motivo_alta_w	smallint;
qt_declaracao_w		bigint;
qt_declaracao_lib_w	bigint;
ie_alta_obito_w		varchar(1);

BEGIN
 
ie_alta_obito_w := Obter_param_Usuario(916, 248, obter_perfil_ativo, obter_usuario_ativo, obter_estabelecimento_ativo, ie_alta_obito_w);
 
ie_exige_declaracao_w := 'N';
 
if (coalesce(nr_atendimento_p,0) > 0) then 
 
	select	coalesce(max(cd_motivo_alta),0) 
	into STRICT	cd_motivo_alta_w 
	from	atendimento_paciente 
	where 	nr_atendimento = nr_atendimento_p;
	 
	select	count(*) 
	into STRICT	qt_declaracao_w 
	from	declaracao_obito 
	where 	nr_atendimento = nr_atendimento_p;
	 
	select	count(*) 
	into STRICT	qt_declaracao_lib_w 
	from	declaracao_obito 
	where 	nr_atendimento = nr_atendimento_p 
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '') 
	and	coalesce(dt_inativacao::text, '') = '';
	 
	 
	if (cd_motivo_alta_w > 0) and 
		((ie_alta_obito_w = 'S' and qt_declaracao_w = 0) or (ie_alta_obito_w = 'L' and qt_declaracao_lib_w = 0))	then 
	 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ie_exige_declaracao_w 
		from	motivo_alta 
		where	cd_motivo_alta = cd_motivo_alta_w 
		and	ie_obito = 'S' 
		and	ie_exige_declaracao = 'S';
	 
	end if;
 
end if;
 
return	ie_exige_declaracao_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
