CREATE OR REPLACE FUNCTION obter_existe_agend_esp (cd_agenda_p bigint, dt_agenda_p timestamp, nr_min_duracao_p bigint) RETURNS varchar AS $body$
DECLARE

			
hr_atual_w		timestamp;			
ie_retorno_w		varchar(1) := 'N';
hr_inicial_w		timestamp;
hr_final_w		timestamp;
nr_seq_turno_esp_w	bigint;
nr_minuto_intervalo_w	bigint;
hr_fim_w		timestamp;

			
c01 CURSOR FOR
	SELECT 	ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(dt_agenda_p, coalesce(hr_inicial, '00:00')),
		ESTABLISHMENT_TIMEZONE_UTILS.dateAtTime(dt_agenda_p,coalesce(hr_final, '00:00')),
		nr_minuto_intervalo
	from 	agenda_Turno_Esp a
	where 	cd_agenda     	= cd_agenda_p
	and	obter_se_turno_esp_agecons_vig(dt_agenda,dt_agenda_fim,dt_agenda_p) = 'S'
	and	to_char(hr_inicial,'hh24:mi:ss') < to_char(hr_final,'hh24:mi:ss')
	and	nr_minuto_intervalo	= nr_min_duracao_p;
	

BEGIN

open c01;
loop
fetch c01 into
	hr_inicial_w,
	hr_final_w,
	nr_minuto_intervalo_w;	
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	
	hr_atual_w	:= hr_inicial_w;
	while(hr_atual_w	< hr_final_w) loop
		begin
		hr_fim_w	:= hr_atual_w + (nr_minuto_intervalo_w - 1) /1440;
	
		if (to_char(dt_agenda_p ,'hh24:mi')  = to_char(hr_atual_w,'hh24:mi') )then
			ie_retorno_w := 'S';
		end if;
		
		hr_atual_w	:= hr_atual_w + (nr_minuto_intervalo_w / 1440);	
		end;
	end loop;	
	
	end;
end loop;
close c01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
