CREATE OR REPLACE FUNCTION obter_existe_marca_mat_estab ( cd_material_p bigint, ds_marca_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_marca_w		bigint;
qt_existe_w		bigint;
ie_existe_w		varchar(1) := 'N';
ds_marca_w		varchar(255);


BEGIN

select	count(1)
into STRICT	qt_existe_w
from	marca a,
	material_marca b
where	b.nr_sequencia = a.nr_sequencia
and	b.cd_material = cd_material_p
and	((coalesce(b.cd_estabelecimento::text, '') = '') or (b.cd_estabelecimento = cd_estabelecimento_p))
and	((substr(upper(elimina_acentuacao(substr(CASE WHEN b.cd_referencia='' THEN a.ds_marca  ELSE a.ds_marca || '(' || b.cd_referencia || ')' END ,1,255))),1,30) = substr(upper(elimina_acentuacao(ds_marca_p)),1,30)) or (substr(upper(elimina_acentuacao(a.ds_marca)),1,30) = substr(upper(elimina_acentuacao(ds_marca_p)),1,30)));

if (qt_existe_w > 0) then
	ie_existe_w := 'S';
end if;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
