CREATE OR REPLACE FUNCTION obter_existe_nf_pj_periodo (cd_cgc_p text, dt_inicio_p timestamp, dt_fim_p timestamp) RETURNS varchar AS $body$
DECLARE

qt_existe_w	bigint;
vl_retorno_w	varchar(1) := 'N';

BEGIN
select	count(*)
into STRICT	qt_existe_w
from	nota_fiscal
where	((cd_cgc = cd_cgc_p) or (cd_cgc_emitente = cd_cgc_p))
and	dt_entrada_saida between dt_inicio_p and fim_dia(dt_fim_p)
and	ie_situacao = 1;

if (qt_existe_w > 0) then
	vl_retorno_w := 'S';
end if;

return	vl_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
