CREATE OR REPLACE FUNCTION obter_existe_obj_schem_regra ( nr_seq_obj_schematic_p bigint, cd_estabelecimento_p bigint, cd_perfil_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w	bigint;
qt_regra_w		bigint;


BEGIN

begin
select	1
into STRICT	qt_regra_w
from	obj_schem_order
where	nr_seq_obj_schematic = nr_seq_obj_schematic_p  LIMIT 1;
exception
when others then
	qt_regra_w	:= 0;
end;

if (qt_regra_w >= 1) then
	begin
	select	a.nr_sequencia
	into STRICT	nr_sequencia_w
	from	obj_schem_order a
	where	a.nr_seq_obj_schematic = nr_seq_obj_schematic_p
	and	exists (SELECT	1
		from	obj_schem_order_usuario x
		where	x.nr_seq_regra	= a.nr_sequencia
		and	x.nm_usuario_regra = nm_usuario_p)  LIMIT 1;
	exception
	when others then
		begin
		select	a.nr_sequencia
		into STRICT	nr_sequencia_w
		from	obj_schem_order a
		where	a.nr_seq_obj_schematic = nr_seq_obj_schematic_p
		and	exists (SELECT	1
			from	obj_schem_order_perfil x
			where	x.nr_seq_regra	= a.nr_sequencia
			and	x.cd_perfil = cd_perfil_p)  LIMIT 1;
		exception
		when others then
			begin
			select	a.nr_sequencia
			into STRICT	nr_sequencia_w
			from	obj_schem_order a
			where	a.nr_seq_obj_schematic = nr_seq_obj_schematic_p
			and	exists (SELECT	1
				from	obj_schem_order_estab x
				where	x.nr_seq_regra	= a.nr_sequencia
				and	x.cd_estabelecimento = cd_estabelecimento_p)  LIMIT 1;
			exception
			when others then
				null;
			end;
		end;
	end;
end if;

if (coalesce(nr_sequencia_w::text, '') = '') then
        return 'N';
    else
        return 'S';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
