CREATE OR REPLACE FUNCTION obter_extenso_bolivia (vl_valor bigint) RETURNS varchar AS $body$
DECLARE


vl_valor_string 	varchar(255);
vl_dizima			double precision;
ds_reais_w			varchar(20) := obter_desc_expressao(1044664); --Reais
ds_real_w			varchar(20) := obter_desc_expressao(1044662); --Real
ds_valor_invalido_w	varchar(20) := obter_desc_expressao(621074); --Valor invalido
ds_com_w            varchar(20) := obter_desc_expressao(623281); --com
BEGIN
	vl_valor_string := obter_numero_extenso(vl_valor, 'ME') || '';

	vl_dizima := (vl_valor - trunc(vl_valor))*100;
	if (vl_dizima >= 0) then
		if (vl_dizima = 1) then 
			vl_valor_string := vl_valor_string || ' ' || ds_com_w || ' ' || to_char(vl_dizima) || '/100 ' || ds_real_w;
		else
			vl_valor_string := vl_valor_string || ' ' || ds_com_w || ' ' || to_char(vl_dizima) || '/100 ' || ds_reais_w;
		end if;
	else
		if (vl_valor = 1) then 
			vl_valor_string := vl_valor_string || ' ' || ds_real_w;
		else
			vl_valor_string := vl_valor_string || ' ' || ds_reais_w;
		end if;
	end if;
	
	return rtrim(vl_valor_string);
exception
  when others then
    return(ds_valor_invalido_w);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
