CREATE OR REPLACE FUNCTION obter_fabric_material ( cd_material_p text, cd_marca_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_fabric  mat_fabricante.nr_sequencia%type;


BEGIN
	if (coalesce(cd_marca_p::text, '') = '') then
		select	nr_seq_fabric
		into STRICT	nr_seq_fabric
		from	material
		where cd_material = cd_material_p;
		
		if (coalesce(nr_seq_fabric::text, '') = '') then
			select 	coalesce(max(cd_fabricante), '0')
			into STRICT	nr_seq_fabric
			from 	material_marca
			where 	cd_material = cd_material_p
			and		(cd_fabricante IS NOT NULL AND cd_fabricante::text <> '');
		end if;
	else
		select 	coalesce(max(cd_fabricante), '0')
		into STRICT	nr_seq_fabric
		from 	material_marca
		where 	cd_material = cd_material_p
		and		nr_sequencia = cd_marca_p;
		
		if (coalesce(nr_seq_fabric::text, '') = '') then
			select	nr_seq_fabric
			into STRICT	nr_seq_fabric
			from	material
			where cd_material = cd_material_p;
		end if;
		
	end if;	
	
return nr_seq_fabric;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
