CREATE OR REPLACE FUNCTION obter_fabric_mat_simpro_estab2 (cd_estabelecimento_p bigint, cd_material_p bigint, dt_base_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_resultado_w		varchar(20);


BEGIN

select	max(a.cd_fabricante)
into STRICT	ds_resultado_w
from	simpro_cadastro a,
	material_simpro b
where	a.cd_simpro = b.cd_simpro
and	b.cd_material = cd_material_p
and 	dt_base_p between coalesce(b.dt_vigencia, dt_base_p) and coalesce(b.dt_final_vigencia, dt_base_p)
and	b.cd_estabelecimento = cd_estabelecimento_p;

if (coalesce(ds_resultado_w::text, '') = '') then
	select	max(a.cd_fabricante)
	into STRICT	ds_resultado_w
	from	simpro_cadastro a,
		material_simpro b
	where	a.cd_simpro = b.cd_simpro
	and 	dt_base_p between coalesce(b.dt_vigencia, dt_base_p) and coalesce(b.dt_final_vigencia, dt_base_p)
	and	b.cd_material = cd_material_p;
end if;

RETURN ds_resultado_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
