SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_fab_lab_tipo_mat ( cd_material_p bigint, nr_seq_bras_preco_p bigint, nr_seq_mat_simp_p bigint, nr_seq_tuss_mat_item_p bigint, ie_origem_preco_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ie_origem_preco_w	smallint;
cd_laboratorio_w	brasindice_preco.cd_laboratorio%type;
ds_laboratorio_w	varchar(255);
cd_fabricante_w		simpro_cadastro.cd_fabricante%type;
ds_fabricante_w		varchar(255);
ie_tipo_material_w	material.ie_tipo_material%type;
ds_fabricante_tuss_w	varchar(255);
ds_laboratorio_tuss_w 	varchar(255);


BEGIN

/*IE_ORIGEM_PRECO
2 - Tabela de Preços
1 - Brasíndice
4 - Simpro
*/
cd_laboratorio_w:= '';
ds_laboratorio_w:= '';
cd_fabricante_w := '';
ds_fabricante_w := '';
ds_fabricante_tuss_w := '';
ds_laboratorio_tuss_w:= '';

if (ie_origem_preco_p = 1) then

	select	max(cd_laboratorio)
	into STRICT	cd_laboratorio_w
	from	brasindice_preco
	where	nr_sequencia = nr_seq_bras_preco_p;

	if (cd_laboratorio_w IS NOT NULL AND cd_laboratorio_w::text <> '') then

		select 	coalesce(max(ds_laboratorio), wheb_mensagem_pck.get_texto(297045)||' :' || cd_laboratorio_w)
		into STRICT 	ds_laboratorio_w
		from 	brasindice_laboratorio
		where 	cd_laboratorio = cd_laboratorio_w;

	end if;

	ds_retorno_w	:= ds_laboratorio_w;

elsif (ie_origem_preco_p = 4) then

	select	max(a.cd_fabricante)
	into STRICT	cd_fabricante_w
	from	simpro_cadastro a,
			material_simpro b
	where	a.cd_simpro = b.cd_simpro
	and		b.cd_material = cd_material_p
	and 	b.nr_sequencia = nr_seq_mat_simp_p;

	if (coalesce(cd_fabricante_w::text, '') = '') then

		select	max(a.cd_fabricante)
		into STRICT	ds_fabricante_w
		from	simpro_cadastro a,
				material_simpro b
		where	a.cd_simpro = b.cd_simpro
		and		b.cd_material = cd_material_p
		and 	b.nr_sequencia = nr_seq_mat_simp_p;

	end if;

	ds_retorno_w	:= ds_fabricante_w;

elsif (ie_origem_preco_p = 2) then

	select 	max(ie_tipo_material)
	into STRICT	ie_tipo_material_w
	from 	material
	where 	cd_material = cd_material_p;

	if (ie_tipo_material_w in (1,5,7,10)) then -- Material
		select	max(ds_fabricante)
		into STRICT	ds_fabricante_tuss_w
		from 	tuss_material_item
		where 	nr_sequencia = nr_seq_tuss_mat_item_p;

		ds_retorno_w	:= ds_fabricante_tuss_w;

	elsif (ie_tipo_material_w in (0,2,3,4,6,8,9)) then -- Medicamento
		select	max(ds_laboratorio)
		into STRICT	ds_laboratorio_tuss_w
		from 	tuss_material_item
		where 	nr_sequencia = nr_seq_tuss_mat_item_p;

		ds_retorno_w	:= ds_laboratorio_tuss_w;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_fab_lab_tipo_mat ( cd_material_p bigint, nr_seq_bras_preco_p bigint, nr_seq_mat_simp_p bigint, nr_seq_tuss_mat_item_p bigint, ie_origem_preco_p bigint) FROM PUBLIC;

