SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_faixa_etaria_pac ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_idade_w		bigint;
ds_retorno_w	varchar(50);

BEGIN
/*
D = descrição da faixa etária
C = código da faixa etária
*/
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	(substr(obter_idade(x.dt_nascimento, clock_timestamp(), 'A'),1,10))::numeric
	into STRICT	qt_idade_w
	from	pessoa_fisica x
	where	x.cd_pessoa_fisica = cd_pessoa_fisica_p;
	begin

		select	CASE WHEN ie_opcao_p='D' THEN qt_idade_min||' - '||qt_idade_max WHEN ie_opcao_p='C' THEN NR_SEQUENCIA END
		into STRICT	ds_retorno_w
		from	faixa_etaria_pac
		where	qt_idade_w between qt_idade_min and qt_idade_max
		and	ie_situacao = 'A';

	exception
	when others then
		if (ie_opcao_p = 'D') then
			ds_retorno_w:= wheb_mensagem_pck.get_texto(785099);
		elsif (ie_opcao_p = 'C') then
			ds_retorno_w:=0;
		end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
