CREATE OR REPLACE FUNCTION obter_faixa_idade_escala_elpo (cd_pessoa_fisica_p bigint) RETURNS bigint AS $body$
DECLARE

	idade_pf_w bigint;

BEGIN

if ((cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and cd_pessoa_fisica_p > 0) then
	select coalesce(OBTER_IDADE_PF(cd_pessoa_fisica_p,null,'A'),0)
	into STRICT idade_pf_w
	;

	if (idade_pf_w >= 0) then

		if (idade_pf_w >= 18 and idade_pf_w <= 39) then
			return 1;
		elsif (idade_pf_w >= 40 and idade_pf_w <= 59) then
			return 2;
		elsif (idade_pf_w >= 60 and idade_pf_w <= 69) then
			return 3;
		elsif (idade_pf_w >= 70 and idade_pf_w <= 79) then
			return 4;
		elsif (idade_pf_w >= 80) then
			return 5;
		else
			return 0;
		end if;

	end if;

end if;

end;
$body$
