CREATE OR REPLACE FUNCTION obter_faixa_pendencias (qt_dias_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN
select	ds_valor
into STRICT	ds_retorno_w
from (
	SELECT	'1' ds_valor
	
	where	qt_dias_p < 7
	
union all

	SELECT	'2' ds_valor
	
	where	qt_dias_p between 8 and 14
	
union all

	select	'3' ds_valor
	
	where	qt_dias_p > 15
	) alias0;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
