CREATE OR REPLACE FUNCTION obter_familia_grupo ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_agenda_int_w		bigint;
nm_familia_w		varchar(60);


BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	a.nr_seq_agenda_int
	into STRICT	nr_seq_agenda_int_w
	from	agendamento_coletivo a
	where	a.nr_sequencia = (	SELECT	max(b.nr_seq_agend_coletiva)
				from	agenda_consulta b
				where	b.nr_atendimento = nr_atendimento_p);

	select	a.nm_familia
	into STRICT	nm_familia_w
	from	agenda_integrada a
	where	a.nr_sequencia = (	SELECT	max(b.nr_seq_agenda_int)
				from	agenda_integrada_item b
				where	b.nr_sequencia = nr_seq_agenda_int_w);
end if;

return	nm_familia_w;

end;
